INSERT INTO `wp_p95jxhnxkz_posts` VALUES (1005, 1, '2019-01-10 04:10:03', '2019-01-10 02:10:03', 'Погрузись в виртуальную реальность в вирутальной реальности.\r\nВам предстоит надеть на себя второй по счёту VR-шлем - это сложно передать словами.\r\nЗдесь ты почувствуешь себя настоящим Нео из фильма "Матрица", ведь теперь тебе подвластен контроль над временем.\r\nПлюсы:\r\n| Игра позволяет ощутить себя героем зубодробительного боевика\r\n| Игра переносит Вас в самый эпицентр боя\r\n| В игре не ощущается укачивание или другие побочные эффекты\r\n| Для достижения победы придётся активно задействовать мозг', 'Super Hot', '', 'trash', 'closed', 'closed', '', 'super-hot__trashed', '', '', '2019-01-10 04:19:06', '2019-01-10 02:19:06', '', 0, 'https://blackfoxvr.com/?post_type=games&#038;p=1005', 0, 'games', '', 0); 
