INSERT INTO `wp_p95jxhnxkz_posts` VALUES (399, 1, '2018-12-05 19:12:01', '2018-12-05 19:12:01', 'Игра Abe VR – основана на сюжете одноименного хоррора. Там вы встречаетесь с кровожадным роботом, который перебирает в своих холодных руках различные хирургические инструменты, выбирая, чем же он будет препарировать свою подружку, которая в свою очередь кричит от ужаса. Станьте непосредственным участником аналогичной картины в виртуальной реальности...если хватит смелости!', 'Abe VR', '', 'trash', 'closed', 'closed', '', 'test__trashed', '', '', '2019-01-10 00:24:56', '2019-01-09 22:24:56', '', 0, 'https://blackfoxvr.com/?post_type=games&#038;p=399', 0, 'games', '', 0); 
