INSERT INTO `wp_p95jxhnxkz_posts` VALUES (763, 1, '2018-12-11 10:45:33', '2018-12-11 10:45:33', 'Это приключенческая игра платформер, является эксклюзивом для очков виртуальной реальности Oculus Rift. Вам вместе с лисенком по имени Лаки, предстоит путешествовать по красочному миру, преодолевать препятствия, собирать сокровища, решать загадки и встретиться с массой разнообразных противников. Эта игра не оставит равнодушными как детей, так и взрослых! )', 'Lucky\'s Tale', '', 'trash', 'closed', 'closed', '', 'luckys-tale__trashed', '', '', '2019-01-10 03:56:59', '2019-01-10 01:56:59', '', 0, 'https://blackfoxvr.com/?post_type=games&#038;p=763', 0, 'games', '', 0); 
